import React, { useState } from "react";
import { AiFillCaretLeft, AiFillCaretRight } from "react-icons/ai";

const Projects = () => {
  let [projects, setProjects] = useState([
    {
      id: 1,
      project_name: "COVID19-TRACKER",
      url: "http://covid_19_tracker.surge.sh/",
      repo_url: "https://www.github.com/ehsanshakil",
    },
    {
      id: 2,
      project_name: "QUIZ-APP",
      url: "https://quiz-app-project.netlify.app/",
      repo_url: "https://www.github.com/ehsanshakil",
    },
  ]);
  let [number, setNumber] = useState(1);
  return (
    <div className="projects">
      <h1>Projects</h1>
      <div className="project">
        <button className="arrow" onClick={() => setNumber(--number)}>
          <AiFillCaretLeft size="30" />
        </button>
        {projects.length >= number && number > 0 && number === 2
          ? projects
              .filter((project) => project.id === number)
              .map((project, index) => (
                <div key={index} className="projectDetails">
                  <h3>Project Name: {project.project_name}</h3>
                  <button className="projectButton">
                    <a
                      href={project.url}
                      alt="Project URL"
                      target="_blank"
                      rel="noreferrer"
                    >
                      <h3>Preview</h3>
                    </a>
                  </button>
                  <button className="projectButton">
                    <a
                      href={project.repo_url}
                      alt="Repo URL"
                      target="_blank"
                      rel="noreferrer"
                    >
                      <h3>Repository</h3>
                    </a>
                  </button>
                </div>
              ))
          : setNumber(2)}
        <button className="arrow" onClick={() => setNumber(++number)}>
          <AiFillCaretRight size="30" />
        </button>
      </div>
    </div>
  );
};

export default Projects;
